SELECT CourseOffers.CourseOfferId, Courses.CourseId, Year, Quartile, CourseName, CourseDescription, Degrees.DegreeId, ECTS, Dept, DegreeDescription, TotalECTS, Teachers.TeacherId, TeacherName, Address, BirthyearTeacher, Gender FROM CourseOffers LEFT JOIN Courses ON Courses.CourseId = CourseOffers.CourseId LEFT JOIN Degrees ON Degrees.DegreeId = Courses.DegreeId LEFT JOIN TeacherAssignmentsToCourses as tc ON CourseOffers.CourseOfferId = tc.CourseOfferId LEFT JOIN Teachers ON Teachers.TeacherId = tc.TeacherId WHERE CourseOffers.CourseOfferId = 1;
SELECT sa.CourseOfferId, CourseId, Year, Quartile, Students.StudentId, StudentName, Address, BirthYearStudent, Gender, Degrees.DegreeId, Dept, DegreeDescription, TotalECTS FROM StudentAssistants as sa LEFT JOIN CourseOffers ON CourseOffers.CourseOfferId = sa.CourseOfferId LEFT JOIN StudentRegistrationsToDegrees as srtd ON srtd.StudentRegistrationId = sa.StudentRegistrationId LEFT JOIN Students ON Students.StudentId = srtd.StudentId LEFT JOIN Degrees ON Degrees.DegreeId = srtd.DegreeId WHERE sa.StudentRegistrationId=3;
SELECT AVG(Grade) FROM CourseRegistrations WHERE CourseRegistrations.StudentRegistrationId=3;
